mapper.select:
SELECT name, S_grade, A_grade, B_grade, C_grade
FROM makeplayerpercent
WHERE rating = ${rating }

mapper.insert:
INSERT INTO team (name, team_key, rating, tendency, money, grade)
VALUES (#{team_name}, uuid(), #{rating}, #{tendency}, 0, 'C')

SELECT team_key FROM team WHERE name = #{team_name}

mapper.update:
    UPDATE player
	SET
		team_key = #{team_key}
	WHERE 
		user_key = #{user_key}

    UPDATE userinfo
	SET
		team_key = #{team_key}
	WHERE 
		user_key = #{user_key}

    UPDATE player
	SET
		name = #{name}
	WHERE
		player_key = #{player_key}

    UPDATE player
	SET
		back_number = #{num}
	WHERE
		player_key = #{player_key}
